mapper.select:
SELECT notice_id AS noticeId, admin_no AS adminNo, fk_hotel_id AS fkHotelId, title, content, is_top AS isTop, created_at AS createdAt FROM NOTICES
fk_hotel_id = #{hotelId}
ORDER BY is_top DESC, created_at DESC

mapper.insert:
INSERT INTO NOTICES (
    notice_id, admin_no, fk_hotel_id, title, content, is_top
) VALUES (
    SEQ_NOTICE_ID.NEXTVAL, #{adminNo}, #{fkHotelId}, #{title}, #{content}, #{isTop}
)

mapper.update:
UPDATE NOTICES SET
    title = #{title},
    content = #{content},
    is_top = #{isTop},
    fk_hotel_id = #{fkHotelId}
WHERE notice_id = #{noticeId}
